sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {
    },

	isDatePast: function(strDate) {
		// Create GlideDateTime objects for current date and specified date.
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		// Return true if the testdate is before rightnow; false otherwise.
		var before = testdate.before(rightnow);
		// gs.info("NeedItUtils: before=" + before);
		return before;
	},

	// Returns true if the specified date is today; false otherwise.
	// Only the date is considered; time is ignored.
	isDateToday: function(strDate) {
		var rightnow = new GlideDateTime();
		var testdate = new GlideDateTime(strDate);

		var now = rightnow.getLocalDate();
		var test = testdate.getLocalDate();

		var today = test.compareTo(now) == 0;
		return today;
	},

    type: 'NeedItUtils'
};
